import React, { Component } from 'react'
import ReactTable from "react-table";  
import Datatable from "react-bs-datatable";

export default class Userdaata extends Component {
    componentDidMount(){
       this.fetchAll();
    }
    constructor(props){
        super(props)
        this.header = [];
        this.state = { data:''}
        this.fetchAll = this.fetchAll.bind(this);
    }

    fetchAll(){
        fetch('http://localhost:8888/.netlify/functions/fetchAll')
        .then(response => response.json())
        .then(data => {
         this.setState({data:data})
         console.log(this.state.data)
        });
    }

  render() {

    const customLabels = {
      first: "<<",
      last: ">>",
      prev: "prev",
      next: "next",
      show: "Display",
      entries: "rows",
      noResults: "There is no data available"
    };
    this.header = [
        { title: "Name", prop: "selectOption" },
        { title: "Email", prop: "fileNames", sortable: true, filterable: true },
    ];
    let userData;
    let name='';
    let Email = '';
    userData = this.state.data.length>0
    ? this.state.data.map((item, i) => {
      name=item.data['name'];
      Email=item.data['email']
      return {
        name:name,
        Email:Email
      };
    }):''
    return (
      <div className='settings-wrapper'>
        <Datatable
            tableHeader={this.header}
            tableBody={userData}
            keyName="relateTable"
            rowsPerPageOption={0}
            tableClass="striped hover responsive tablecolor relateAlign"
            initialSort={{ prop: "Title", isAscending: true }}
            labels={customLabels}
                //   onSort={onSort}
        />
      </div>
    )
  }
}
