import React, { Component } from 'react'
import ReactTable from "react-table";  
import Datatable from "react-bs-datatable";
// import Pagination from "react-js-pagination";

export default class Userdaata extends Component {
    componentDidMount(){
      this.fetchAll();
    }
    constructor(props){
        super(props)
        this.header = [];
        this.state = { data:[],activePage:1}
        this.fetchAll = this.fetchAll.bind(this);
    }
    fetchAll(){
        fetch('http://localhost:8888/.netlify/functions/fetchAll')
        .then(response => response.json())
        .then(data => {
         this.setState({data:data})
        });
    }

  render() {
    console.log(this.state.data.length)
    const customLabels = {
      first: "<<",
      last: ">>",
      prev: "prev",
      next: "next",
      show: "Display",
      entries: "rows",
      noResults: "There is no data available"
    };
    this.header = [
        { title: "Name", prop: "name" },
        { title: "Email", prop: "Email" }
    ];
    let userData;
    let name='';
    let Email = '';
    userData = this.state.data.length>0
    ? 
      this.state.data.map((item, i) => {
      if(item.data['name'])
      {
        name=item.data['name'];
      }
      if(item.data['email'])
      {
        Email=item.data['email']
      }
      return {
        name:name,
        Email:Email
      };
      }):"";
    return (
        <div className={"table-responsive"}>
            {this.state.data.length > 0 && (
              <Datatable
                tableHeader={this.header}
                tableBody={userData}
                keyName="relateTable"
                rowsPerPageOption={0}
                tableClass="striped hover responsive tablecolor relateAlign"
                initialSort={{ prop: "Title", isAscending: true }}
                labels={customLabels}
                  // onSort={onSort}
              />
            )}
      </div>
    )
  }
}
