import React, { Component } from 'react'
import ReactTable from "react-table";  
import Datatable from "react-bs-datatable";
// import Pagination from "react-js-pagination";

export default class Userdaata extends Component {
    componentDidMount(){
      this.fetchAll();
    }
    constructor(props){
        super(props)
        this.header = [];
        this.state = { data:[],activePage:1}
        this.fetchAll = this.fetchAll.bind(this);
    }
    fetchAll(){
        fetch('http://localhost:8888/.netlify/functions/fetchAll')
        .then(response => response.json())
        .then(data => {
         this.setState({data:data})
        });
    }

    renderTableData() {
      return this.state.data.map((student, index) => {
        console.log(student,'st')
         const { name,email } = student.data //destructuring
         console.log(name,email)
         return (
            <tr key={email}>
               <td>{name}</td>
               <td>{email}</td>
            </tr>
         )
      })
   }

   render() {
    return (
       <div>
          <h1 id='title'>React Dynamic Table</h1>
          <table id='students'>
             <tbody>
                {this.renderTableData()}
             </tbody>
          </table>
       </div>
    )
 }
