import React, { Component } from 'react'
import ReactTable from "react-table";  
import Datatable from "react-bs-datatable";

export default class Userdaata extends Component {
    componentDidMount(){
        this.fetchAll()
    }
    constructor(props){
        super(props)
        this.header = [];
        this.state = {}
        this.fetchAll = this.fetchAll.bind(this);
    }

    fetchAll(){
        const requestOptions = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name: `${this.state.name}`,email:`${this.state.email}` })
        };
        fetch('http://localhost:8888/.netlify/functions/fetchAll').then((response) => {
            return response.json()
        })
    }

  render() {
    this.header = [
        { title: "Name", prop: "selectOption" },
        { title: "Email", prop: "fileNames", sortable: true, filterable: true },
    ];
    return (
      <div className='settings-wrapper'>

      </div>
    )
  }
}
