import React, { Component } from 'react'



export default class AddUser extends Component {
    constructor(props){
        super(props)
        this.state ={
            name:"",
            email:""
        }
        this.userInput = this.userInput.bind(this);
        this.SignUp  = this.SignUp.bind(this);
    }

    userInput(e){
        if(e.target.name == 'name')
        {
            this.setState({ name:e.target.value})
        }
        else if(e.target.name == 'email')
        {
            this.setState({ email:e.target.value})
        }
    }

    SignUp(event){
        axios({
            method: "post",
            url: ``,
            headers: {
              "Content-type": "application/x-www-form-urlencoded"
            },
            data: `name=${this.state.name}&email=${this.state.email}`
          })
          .then(response =>{
              console.log(response)
          })
    }

    render() {
      return(
          <div>
              <h3>React CRUD using FAUNA</h3>
              <center>
                <div class="form-group col-md-4">
                    <input type="text" className="form-control"  value={this.state.name} placeholder="Enter Name" onChange={e=>this.userInput(e)} name='name' />
                </div>
                <div class="form-group col-md-4 mt-3">
                    <input type="email" className="form-control"  value={this.state.email} placeholder="Enter Email" onChange={e=>this.userInput(e)} name='email' />
                </div>
                    <button type="submit" class="btn btn-primary mt-5" onClick={ e=>this.SignUp(e)}>Submit</button>
                </center>
          </div>
      )
    }
  }
