import React, { Component } from 'react'



export default class AddUser extends Component {
    constructor(props){
        super(props)
        this.state ={
            name:"",
            email:""
        }
        this.userInput = this.userInput.bind(this);
    }

    userInput(e){
        if(e.target.name == 'name')
        {
            this.setState({ name:e.target.value})
        }
        else if(e.target.name == 'name')
        {
            this.setState({ email:e.target.value})
        }
    }

    render() {
      return(
          <div>
              <h3>React CRUD using FAUNA</h3>
              <center>
              <form method="post">
                <div class="form-group col-md-4">
                    <input type="text" class="form-control"  value={this.state.name} placeholder="Enter Name" onChange={e=>this.userInput(e)} name='name' />
                </div>
                <div class="form-group col-md-4 mt-3">
                    <input type="email" class="form-control"  value={this.state.email} placeholder="Enter Email" onChange={e=>this.userInput(e)} name='email' />
                </div>
                    <button type="submit" class="btn btn-primary mt-5">Submit</button>
                </form>
                </center>
          </div>
      )
    }
  }
